import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { debounceTime } from 'rxjs/operators';
import { Router } from '@angular/router';

import { RequestService } from 'src/app/services/request.service';

@Component({
    selector: 'app-form',
    templateUrl: './form.component.html',
    styleUrls: ['./form.component.scss'],
})

export class FormComponent implements OnInit {
    constructor(private svc: RequestService, public router: Router) {}

    comboboxData: any;
    
    form = new FormGroup({
        fieldOne: new FormControl<string>('18', [ Validators.required, myValidator ]),
        fieldSelect: new FormControl<string>('', [ Validators.required ]),
        fieldCheck: new FormControl<string>('', [ Validators.required ]),
    })

    get fieldOne() {
        return this.form.controls.fieldOne as FormControl   
    } 
    
    ngOnInit(): void {
        this.svc.getComboxFields().subscribe(data => {
            this.comboboxData = data
        })
    }

    handleSubmit() {
        console.log(this.form.value)
        this.router.navigate(['/main']);
    }
}
 
// Функция валидации
function myValidator(formControl: FormControl) {

    if (formControl.value < 18 || formControl.value > 120) {
        return { myValidator: { message: 'Неправельный диапозон' } }
    }

    return null
}
