import { Component, OnInit } from '@angular/core'
import { FormControl, Validators } from '@angular/forms'
import { debounceTime } from 'rxjs/operators'

@Component({
    selector: 'app-form',
    templateUrl: './form.component.html',
    styleUrls: ['./form.component.scss'],
})

function myValidator(formControl: FormControl) {

    if (formControl.value.length === 0) {
        return { myValidator: { message: 'Поле не может быть пустым!' } }
    }

    return null
}

export class FormComponent implements OnInit {
    nameControl: FormControl;

    ngOnInit() {
         this.nameControl = new FormControl('', [ Validators.required, myValidator ]);
         this.nameControl.valueChanges
         .pipe(
            debounceTime(1000)
         )
         .subscribe((value) => console.log(value))
         this.nameControl.statusChanges.subscribe((status) => {
            console.log(this.nameControl.errors);
            console.log(status);
         })
    }
}
